import React, { useState, useEffect } from 'react';
import api from './services/api';
import './global.css';
import './App.css';
import './Sidebar.css';
import './Main.css';



//componente: bloco isolado de html, css e js, o qual não interfere no restante da aplicação
//propriedade: informações que um componete pai passa para um componente filho
//estado: informações mantidas pelo componente (lembrar: imutabilidade)

function App() {

  const [github_username, setGithubUsername] = useState('');
  const [techs, setTechs] = useState('');

  const [latitude, setLatitude] = useState('');
  const [longitude, setlongitude] = useState('');

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords; 

        setLatitude(latitude);
        setlongitude(longitude);
      },
      (error) => {
        console.log(error);
      },
      {
        timeout: 30000,
      }
    )
  }, []);

  async function handleAddDev(e){
    e.preventDefault();

    const response = await api.post('/devs',{
      github_username,
      techs,
      latitude,
      longitude,
    });
  }

  return (
    <div id="app">
      <aside>
        <strong>Cadastrar</strong>
        <form onSubmit={handleAddDev}>
          <div className="input-block">
            <label htmlFor="github_username">Usuário do Github</label>
            <input name="github_username" id="github_username" required value={github_username} onChange={e => setGithubUsername(e.target.value)} />
          </div>

          <div className="input-block">
            <label htmlFor="techs">Tecnologias</label>
            <input name="techs" id="techs" required value={techs} onChange={e => setTechs(e.target.value)}/>
          </div>

          <div className="input-group">
            <div className="input-block">
              <label htmlFor="latitude">Latitude</label>
              <input type="number" name="latitude" id="latitude" required value={latitude} onChange={e => setLatitude(e.target.value)}/>
            </div>
          </div>

          <div className="input-group">
            <div className="input-block">
              <label htmlFor="longitude">Longitude</label>
              <input type="number" name="longitude" id="longitude" required value={longitude} onChange={e => setlongitude(e.target.value)} />
            </div>
          </div>
          <button type="submit">Salvar</button>
        </form>
      </aside>
      <main>
        <ul>
          <li className="dev-item">
            <header>
              <img src="https://avatars2.githubusercontent.com/u/39926697?s=460&v=4" alt="Enderson Rufino" />
              <div className="user-info">
                <strong>Enderson Rufino</strong>
                <span>C#, CSS, JS</span>
              </div>
            </header>
            <p>kkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkk</p>
            <a href="https://github.com/endersonra">Acessar perfil no Github</a>
          </li>

          <li className="dev-item">
            <header>
              <img src="https://avatars2.githubusercontent.com/u/39926697?s=460&v=4" alt="Enderson Rufino" />
              <div className="user-info">
                <strong>Enderson Rufino</strong>
                <span>C#, CSS, JS</span>
              </div>
            </header>
            <p>kkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkk</p>            
            <a href="https://github.com/endersonra">Acessar perfil no Github</a>
          </li>

          <li className="dev-item">
            <header>
              <img src="https://avatars2.githubusercontent.com/u/39926697?s=460&v=4" alt="Enderson Rufino" />
              <div className="user-info">
                <strong>Enderson Rufino</strong>
                <span>C#, CSS, JS</span>
              </div>
            </header>
            <p>kkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkk</p>           
            <a href="https://github.com/endersonra">Acessar perfil no Github</a>
          </li>

          <li className="dev-item">
            <header>
              <img src="https://avatars2.githubusercontent.com/u/39926697?s=460&v=4" alt="Enderson Rufino" />
              <div className="user-info">
                <strong>Enderson Rufino</strong>
                <span>C#, CSS, JS</span>
              </div>
            </header>
            <p>kkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkk</p>
            <a href="https://github.com/endersonra">Acessar perfil no Github</a>
          </li>
        </ul>
      </main>
    </div>
  );
}

export default App;
